package day06

fun main(args: Array<String>) {

    fun part1() {
        for (i in 4..text.length) {
            if (text.subSequence(i - 4, i).toSet().toList() == text.subSequence(i - 4, i).toList()) {
                println(i)
                break
            }
        }
    }
    part1()


    fun part2() {
        for (i in 14..text.length) {
            if (text.subSequence(i - 14, i).toSet().toList() == text.subSequence(i - 14, i).toList()) {
                println(i)
                break
            }
        }
    }
    part2()
}

const val text =
    ""
